import React from 'react';
import ReactDOM from 'react-dom';
import Button from '../Button/Button';
import styles from './ConfirmModal.module.scss'

const ConfirmModal = ({isOpen, question, handleAddLocation, handleClosePopup, confirmText = "Yes", rejectText = "No", className}) => {
    return ReactDOM.createPortal(
        <div className={className}>
            <p>{question}</p>
            <div className={styles.buttons}>
                <Button onClick={handleAddLocation} text={confirmText}/>
                <Button onClick={handleClosePopup} type="secondary" text={rejectText}/>
            </div>
        </div>
    );
};

export default ConfirmModal;
