import React, {useContext, useRef} from 'react';
import styles from './FileUploadButton.module.scss'
import {observer} from "mobx-react-lite";
import {RootStoreContext} from "../../stores/RootStore";

const FileUploadButton = observer(() => {
        const fileInputRef = useRef(null);

        const {
            listStore: {
                addItem
            }
        } = useContext(RootStoreContext)

        const handleFileSelect = (e) => {
            const file = e.target.files[0];
            if (file) {
                const reader = new FileReader();
                reader.onload = (event) => {
                    const result = event.target.result;
                    const lines = result.split('\n').map(line => line.trim());

                    // Exclude the header row
                    const parsedData = lines.slice(1).map(line => {
                        const [latitude, longitude] = line.split(',');
                        return {latitude, longitude};
                    });

                    // Filter out empty lines
                    const filteredData = parsedData.filter(item => item.latitude && item.longitude);

                    console.log(filteredData)
                    addItem(...filteredData);
                };
                reader.readAsText(file);
            }
        }

        return (
            <div className={styles.root}>

                <label htmlFor="file-upload" className={styles.button}>
                    Import Data
                </label>
                <input
                    type="file"
                    id="file-upload"
                    accept=".csv, .xlsx, .xls"
                    ref={fileInputRef}
                    style={{display: 'none'}}
                    onChange={handleFileSelect}
                />
            </div>
        );
    }
);

export default FileUploadButton;
